import { NewTodo } from './Components/NewTodo/NewTodo';
import { useState } from 'react';
import Todos from './Components/Todos/Todos';
import TodoModel from './Models/TodoModel';
function App() {
  const [todo, setTodo] = useState<TodoModel[]>([]);

  function addTodoHandler(todoText: string) {
    const newTodo = new TodoModel(todoText);

    setTodo((prevTodos) => {
      return prevTodos.concat(newTodo);
    });
  }

  function removeTodoHandler(todoId: string) {
    console.log('called from AP.JS');
    setTodo((prevTodos) => {
      return prevTodos.filter(todo => todo.id !== todo)
    })
  }

  return (
    <div>
      <NewTodo onAddTodo={addTodoHandler} />
      <Todos onRemoveTodo={removeTodoHandler} items={todo} />
    </div>
  );
}

export default App;
