import React from 'react';
import TodoClass from '../Models/TodoModel';
import Todo from './TodoItem';
import styles from './Todos.module.cs';
//generic type defined here. Define own props. Can make option by 'items?:'
const Todos: React.FC<{ items: TodoClass[] }> = (props) => {
  return (
    <ul className={styles.todos}>
      {props.items.map((item) => (
        <Todo key={item.id} text={item.text}>
          {item.text}
        </Todo>
      ))}
    </ul>
  );
};

export default Todos;
