import { NewTodo } from './Components/NewTodo';
import { useState } from 'react';
import Todos from './Components/Todos';
import TodoClass from './Models/TodoModel';
function App() {
  const todos = [
    new TodoClass('learn react'),
    new TodoClass('learn typescript'),
  ];

  function handleNewTodo(newTodo: string) {
    todos.push(newTodo);
  }
  return (
    <div>
      <NewTodo onAddTodo={handleNewTodo} />
      <Todos items={todos} />
    </div>
  );
}

export default App;
